<script>
import { ref, onMounted, nextTick } from "vue";
import { useRouter } from "vue-router";

export default {
    components: {
    },
    data(){
        return {
        }
    },
    methods: {
    },
    setup(){
        const myRouter =  useRouter();
        const renderSvgDashArray = ()=>{
                document.querySelector('.loading-container').style.display='none'
                let els = document.querySelectorAll('.loading-container svg path')
                for(let item of els){
                    let totoalLength = Math.floor(item.getTotalLength())
                    item.style.strokeDasharray = totoalLength + 'px'
                    item.style.strokeDashoffset = totoalLength + 'px'
                    item.style.animation = 'start 1.2s forwards 200ms'
                }
                document.querySelector('.loading-container').style.display=''
                setTimeout(() => {
                    myRouter.push('/home')
                }, 2 * 1000);
        }
        onMounted (function(){
            nextTick(()=>{
                renderSvgDashArray()
            })
        })
    }
}
</script>

